import React, { useState } from 'react';
import Movie from './Movie';
import './Carousel.css';

const Carousel = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const movies = [
    { id: 1, title: 'Movie 1' },
    { id: 2, title: 'Movie 2' },
    { id: 3, title: 'Movie 3' },
    // Agrega más películas aquí
  ];

  const slidesToShow = 4; // Número de elementos a mostrar en cada diapositiva

  const totalSlides = Math.ceil(movies.length / slidesToShow);

  const goToNextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide === totalSlides - 1 ? 0 : prevSlide + 1));
  };

  const goToPreviousSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide === 0 ? totalSlides - 1 : prevSlide - 1));
  };

  const startIndex = currentSlide * slidesToShow;
  const visibleMovies = movies.slice(startIndex, startIndex + slidesToShow);

  return (
    <div className="carousel-container">
      <h2>Carousel</h2>
      <div className="carousel-content">
        <button className="carousel-button" onClick={goToPreviousSlide}>Anterior</button>
        <div className="movie-container">
          {visibleMovies.map((movie) => (
            <Movie key={movie.id} movie={movie} />
          ))}
        </div>
        <button className="carousel-button" onClick={goToNextSlide}>Siguiente</button>
      </div>
    </div>
  );
}

export default Carousel;
