import React, { useState, useEffect } from 'react';
import axios from 'axios';
import YouTube from 'react-youtube';
import './detailsMovie.css';
import Comentarios from '../comentarios/comentarios';

const DetailsMovie = ({ movie, handleBackClick }) => {
  const { title, release_date, genres, overview } = movie;
  const [trailerId, setTrailerId] = useState(null);
  const [vermas, setvermas] = useState(false);

  useEffect(() => {
    const fetchTrailer = async () => {
      try {
        const apiKey = '83bc0d3d812780eff004a2baed4eaf17';
        const response = await axios.get(
          `https://api.themoviedb.org/3/movie/${movie.id}/videos?api_key=${apiKey}`
        );
        const trailerData = response.data;
        const trailer = trailerData.results.find((result) => result.site === 'YouTube');
        if (trailer) {
          setTrailerId(trailer.key);
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchTrailer();
  }, [movie.id]);

  return (
    <div className='detalle'>
      <div className='trailer'>
         <YouTube videoId={trailerId} opts={{width: '500px', height: '350px' }} />  
         <p>
          <strong>Fecha de estreno:</strong> {release_date}<br></br>
          <strong>GÃ©nero:</strong> {genres.map((genre) => genre.name).join(', ')}
        </p>
      </div>
    
      <div className='contenido'>
        <h2>{title}</h2>
        <p>
          <strong>Sinopsis:</strong> {overview}
        </p>
        <div className="rating-container">
          <h3>Califica esta pelÃ­cula:</h3>
          <div className="rating-inputs">
            <label>
              <input
                type="radio"
                name="rating"
                value={1}
                
              />
              1
            </label>
            <label>
              <input
                type="radio"
                name="rating"
                value={2}
               
              />
              2
            </label>
            <label>
              <input
                type="radio"
                name="rating"
                value={3}
                
              />
              3
            </label>
            <label>
              <input
                type="radio"
                name="rating"
                value={4}
                
              />
              4
            </label>
            <label>
              <input
                type="radio"
                name="rating"
                value={5}
                
              />
              5
            </label>
          </div>
        </div>
        <div className="button">
          <button className="vote"  >Votar</button>
          <button className="back" onClick={handleBackClick}>Volver</button>
        </div>
      </div>
    <div className="chatComentarios">
    <Comentarios />
    </div>
      
    </div>
  );
};

export default DetailsMovie;
