import React, { useState } from 'react';
import './header.css';

const Header = ({ handleFormVisibilityRegistro, handleFormVisibilityInicioSesion, handleSearch }) => {
  const [searchValue, setSearchValue] = useState('');

  const handleInputChange = (event) => {
    setSearchValue(event.target.value);
  };

  const handleSearchClick = () => {
    handleSearch(searchValue);
  };

  return (
    <div className="header">
      <h1>Películas App</h1>
      <div className="search-container">
        <input
          type="text"
          placeholder="Buscar película..."
          value={searchValue}
          onChange={handleInputChange}
        />
        <button onClick={handleSearchClick}>Buscar</button>
      </div>
      <div className="user-options">
        <button onClick={handleFormVisibilityRegistro}>Registrarse</button>
        <button onClick={handleFormVisibilityInicioSesion}>Iniciar sesión</button>
      </div>
    </div>
  );
};

export default Header;
