import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Movie from '../movie/movie';
import Publicidad from '../publicidad/publicidad';
import DetailsMovie from '../details/detailsMovie';
import Menu from '../menu/menu';
import './carousel.css';

const Carousel = () => {
  const [movies, setMovies] = useState([]);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [showDetails, setShowDetails] = useState(false);
  const [page, setPage] = useState(1);
  const [isLoading, setIsLoading] = useState(false);
  const [genres, setGenres] = useState([
    { id: 1, name: 'Comedia' },
    { id: 2, name: 'Drama' },
    { id: 3, name: 'Romántica' },
    { id: 4, name: 'Ciencia Ficción' },
    { id: 5, name: 'Thriller' },
  ]);

  const fetchMovies = async (sortBy = 'popular') => {
    setIsLoading(true);
    try {
      const apiKey = '83bc0d3d812780eff004a2baed4eaf17';
      const response = await axios.get(`https://api.themoviedb.org/3/movie/${sortBy}?api_key=${apiKey}&page=${page}`);
      const moviesData = response.data.results;
      setMovies((prevMovies) => [...prevMovies, ...moviesData]);
      setIsLoading(false);
    } catch (error) {
      console.error(error);
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchMovies();
  }, [page]);

  const handleDetailsVisibility = (movie) => {
    setSelectedMovie(movie);
    setShowDetails(true);
  };

  const handleBackClick = () => {
    setSelectedMovie(null);
    setShowDetails(false);
  };

  const handleLoadMore = () => {
    setPage((prevPage) => prevPage + 1);
  };

  const handleGenreClick = (genreId) => {
    // Aquí puedes implementar la lógica para filtrar las películas por género
    // Puedes utilizar el genreId para realizar la filtración
    // Por ejemplo, puedes crear un nuevo estado para almacenar las películas filtradas por género
    // y actualizarlo aquí
  };

  const handleNewsClick = () => {
    fetchMovies('now_playing');
  };

  const handleTopRatedClick = () => {
    fetchMovies('top_rated');
  };

  return (
    <div className="carousel">
      <Menu
        genres={genres}
        handleGenreClick={handleGenreClick}
        handleNewsClick={handleNewsClick}
        handleTopRatedClick={handleTopRatedClick}
      />

      {showDetails ? (
        <DetailsMovie movie={selectedMovie} handleBackClick={handleBackClick} />
      ) : (
        <>
          <div className="carousel-grid">
            {movies.map((movie) => (
              <div key={movie.id} className="carousel-content">
                <Movie movie={movie} handleDetailsVisibility={handleDetailsVisibility} />
              </div>
            ))}
          </div>

          {isLoading && <p>Loading...</p>}
          {!isLoading && (
            <div className="load-more-container">
              <Publicidad />
              <button className="load-more-button" onClick={handleLoadMore}>Ver Más ↓</button>
            </div>
          )}
        </>
      )}
    </div>
 

  );
};

export default Carousel;
