import React from 'react';
import './menu.css';

const Menu = ({ genres, handleGenreClick, handleNewsClick, handleTopRatedClick }) => {
    return (
        <div className="genre-cord">
          <h3>Generos:</h3>
          {genres.map((genre) => (
            <button key={genre.id} onClick={() => handleGenreClick(genre.id)}>
              {genre.name}
            </button>
          ))}
          <span>|</span>
          <button onClick={handleNewsClick}>Noticias</button>
          <button onClick={handleTopRatedClick}>Las m√°s votadas</button>
        </div>
      );
      
};

export default Menu;
