import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Movie from '../movie/movie';
import DetailsMovie from '../details/detailsMovie';
import './carousel.css';

const Carousel = () => {
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [showDetails, setShowDetails] = useState(false);
  const [movie1, setMovie1] = useState(null);
  const [movie2, setMovie2] = useState(null);
  const [movie3, setMovie3] = useState(null);
  const [movie4, setMovie4] = useState(null);

  const fetchMovie = async (movieId, setMovie) => {
    const apiKey = '83bc0d3d812780eff004a2baed4eaf17';
    const response = await axios.get(`https://api.themoviedb.org/3/movie/${movieId}?api_key=${apiKey}`);
    const movieData = response.data;
    setMovie(movieData);
  };

  useEffect(() => {
    // IDs de las películas a mostrar en cada div del carrusel
    const movie1Id = 1;
    const movie2Id = 2;
    const movie3Id = 3;
    const movie4Id = 4;

    fetchMovie(movie1Id, setMovie1);
    fetchMovie(movie2Id, setMovie2);
    fetchMovie(movie3Id, setMovie3);
    fetchMovie(movie4Id, setMovie4);
  }, []);

  const handleDetailsVisibility = (movie) => {
    setSelectedMovie(movie);
    setShowDetails(true);
  };

  const handleBackClick = () => {
    setSelectedMovie(null);
    setShowDetails(false);
  };

  return (
    <div className="carousel">
      {showDetails ? (
        <DetailsMovie movie={selectedMovie} handleBackClick={handleBackClick} />
      ) : (
        <>
          <div className="carousel-content">
            {movie1 && <Movie movie={movie1} handleDetailsVisibility={handleDetailsVisibility} />}
          </div>
          <div className="carousel-content">
            {movie2 && <Movie movie={movie2} handleDetailsVisibility={handleDetailsVisibility} />}
          </div>
          <div className="carousel-content">
            {movie3 && <Movie movie={movie3} handleDetailsVisibility={handleDetailsVisibility} />}
          </div>
          <div className="carousel-content">
            {movie4 && <Movie movie={movie4} handleDetailsVisibility={handleDetailsVisibility} />}
          </div>
          <button className="boton" onClick={fetchMovie}>
            &gt;
          </button>
        </>
      )}
    </div>
  );
};

export default Carousel;
