import React, { useState } from 'react';
import './App.css';
import Movie from './componentes/movie';
import MovieDetails from './componentes/movieDetails';





const App = () => {
  const [movieDetailsVisible, setmovieDetailsVisible] = useState(false);

  const verDetalles = () => {
    setmovieDetailsVisible(!movieDetailsVisible);
  };

  const movie = {
    title: 'Título de la película',
    poster_path: '/ruta/al/poster.jpg',
    overview: 'Descripción de la película',
    // Otros datos de la película
  };

  return (
    <div>
      <Movie openMovieDetails={verDetalles} />
      <MovieDetails visible={movieDetailsVisible} movie={movie} />
    </div>
    
  );
};

export default App;
