import React, { useEffect, useState } from 'react';
import axios from 'axios';

let ButtonSiguiente = document.getElementById("siguiente");

const Movie = () => {
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    const fetchMovie = async () => {
      try {
        const apiKey = '83bc0d3d812780eff004a2baed4eaf17';
        const movieId = generarNumero();
        const response = await axios.get(`https://api.themoviedb.org/3/movie/${movieId}?api_key=${apiKey}`);
        setMovie(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchMovie();
  }, []);


  if (!movie) {
    return <div>Cargando...</div>;
  }

  return (
    <div className="card">
      <img className="card-image" src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt={movie.title} />
      <div className="card-content">
        <h1 className="card-title">{movie.title}</h1>
        <button className="card-buttom">Ver mas</button>
        <button className="card-buttom">Calificar</button>
       {/*  <p className="card-description">{movie.overview}</p> */}
      </div>
    </div>

  );
};

function generarNumero() {
  numeroPokemon = Math.floor(Math.random() * 150) + 1;
};

ButtonSiguiente.addEventListener('click',  fetchMovie())

getPokemon();

export default Movie;
