import React, { useState } from 'react';
import './detailsMovie.css';

const DetailsMovie = ({ movie, handleBackClick }) => {
  const { title, release_date, genres, overview, vote_average } = movie;
  const [rating, setRating] = useState(0);

  const handleRatingChange = (value) => {
    setRating(value);
  };

  return (
    <div className='detalle'>
      <img src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`} alt={title} />
      <div className='contenido'>
        <h2>{title}</h2>
        <p>
          <strong>Fecha de estreno:</strong> {release_date}
        </p>
        <p>
          <strong>Género:</strong> {genres.map((genre) => genre.name).join(', ')}
        </p>
        <p>
          <strong>Sinopsis:</strong> {overview}
        </p>
        <p>
          <strong>Calificación:</strong> {vote_average}
        </p>
        <div className="rating-container">
          <p>Califica esta película:</p>
          <div className="rating-inputs">
            <label>
              <input
                type="radio"
                name="rating"
                value={1}
                checked={rating === 1}
                onChange={() => handleRatingChange(1)}
              />
              1
            </label>
            <label>
              <input
                type="radio"
                name="rating"
                value={2}
                checked={rating === 2}
                onChange={() => handleRatingChange(2)}
              />
              2
            </label>
            <label>
              <input
                type="radio"
                name="rating"
                value={3}
                checked={rating === 3}
                onChange={() => handleRatingChange(3)}
              />
              3
            </label>
            <label>
              <input
                type="radio"
                name="rating"
                value={4}
                checked={rating === 4}
                onChange={() => handleRatingChange(4)}
              />
              4
            </label>
            <label>
              <input
                type="radio"
                name="rating"
                value={5}
                checked={rating === 5}
                onChange={() => handleRatingChange(5)}
              />
              5
            </label>
          </div>
        </div>
        <button onClick={handleBackClick}>Volver</button>
      </div>
    </div>
  );
};

export default DetailsMovie;
