import React from 'react';
import './detailsMovie.css';

const DetailsMovie = ({ movie, handleBackClick }) => {
  const { title, director, release_date, genres, overview, poster_path, vote_average, cast, runtime, original_language, origin_country } = movie;

  return (
    <div className='detalle'>
      <img src={`https://image.tmdb.org/t/p/w500/${poster_path}`} alt={title} />
      <div className='contenido'>
        <h2>{title}</h2>
        <p>
          <strong>Fecha de estreno:</strong> {release_date}
        </p>
        <p>
          <strong>Género:</strong> {genres.map((genre) => genre.name).join(', ')}
        </p>
        <p>
          <strong>Duración:</strong> {runtime} minutos
        </p>
        <p>
          <strong>Idioma:</strong> {original_language}
        </p>
        <p>
          <strong>País de origen:</strong> {origin_country.join(', ')}
        </p>
        <p>
          <strong>Sinopsis:</strong> {overview}
        </p>
        <p>
          <strong>Calificación:</strong> {vote_average}
        </p>
        <p>
          <strong>Reparto de actores:</strong> {cast.map((actor) => actor.name).join(', ')}
        </p>
        <button onClick={handleBackClick}>Volver</button>
      </div>
    </div>
  );
};

export default DetailsMovie;
