import React from 'react';

const MovieDetails = ({ visible, movie }) => {
  if (!visible) {
    return null;
  }
  return (
    <div>
      <h1>{movie.title}</h1>
      <img src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt={movie.title} />
      <p>{movie.overview}</p>
      {/* Muestra más información de la película según tus necesidades */}
    </div>
  );
};

export default MovieDetails;
