import React, { useState } from 'react';
import './comentarios.css';

const Comentarios = () => {
  const [comentarios, setComentarios] = useState([]);
  const [nuevoComentario, setNuevoComentario] = useState('');

  const enviarComentario = (event) => {
    setNuevoComentario(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (nuevoComentario.trim() !== '') {
      setComentarios([...comentarios, nuevoComentario]);
      setNuevoComentario('');
    }
  };

  return (
    <div className="comentarios">
      <div className="chat">
        {comentarios.map((comentario, index) => (
          <div key={index} className="mensaje">
            {comentario}
          </div>
        ))}
        {comentarios.length === 0 && <div className="mensaje vacio">No hay comentarios a√∫n</div>}
      </div>
      <form className="formulario-comentario" onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Escribe un comentario"
          value={nuevoComentario}
          onChange={enviarComentario}
        />
        <button type="submit">Enviar</button>
      </form>
    </div>
  );
};

export default Comentarios;
