import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Movie from '../movie/movie';
import Publicidad from '../publicidad/publicidad';
import DetailsMovie from '../details/detailsMovie';
import Menu from '../menu/menu';
import './carousel.css';

const Carousel = () => {
  const [movies, setMovies] = useState([]);
  const [filteredMovies, setFilteredMovies] = useState([]);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [showDetails, setShowDetails] = useState(false);
  const [page, setPage] = useState(1);
  const [isLoading, setIsLoading] = useState(false);
  const [genres, setGenres] = useState([
    { id: 28, name: 'Acción' },
    { id: 12, name: 'Aventura' },
    { id: 16, name: 'Animación' },
    { id: 35, name: 'Comedia' },
    { id: 12, name: 'Aventura' },
    { id: 16, name: 'Animación' },
    // Agrega más géneros aquí
  ]);

  const fetchMovies = async (genreId) => {
    setIsLoading(true);
    try {
      const apiKey = '83bc0d3d812780eff004a2baed4eaf17';
      let url = `https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}&page=${page}`;
      if (genreId) {
        url = `https://api.themoviedb.org/3/discover/movie?api_key=${apiKey}&with_genres=${genreId}&page=${page}`;
      }
      const response = await axios.get(url);
      const moviesData = response.data.results;
      setMovies((prevMovies) => [...prevMovies, ...moviesData]);
      setIsLoading(false);
    } catch (error) {
      console.error(error);
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchMovies();
  }, [page]);

  const handleDetailsVisibility = (movie) => {
    setSelectedMovie(movie);
    setShowDetails(true);
  };

  const handleBackClick = () => {
    setSelectedMovie(null);
    setShowDetails(false);
  };

  const handleLoadMore = () => {
    setPage((prevPage) => prevPage + 1);
  };

  const handleGenreClick = (genreId) => {
    const filteredMovies = movies.filter((movie) => movie.genre_ids.includes(genreId));
    setFilteredMovies(filteredMovies);
    setPage(1); // Reinicia la paginación cuando se selecciona un nuevo género
  };

  return (
    <div className="carousel">
      <Menu genres={genres} handleGenreClick={handleGenreClick} />

      {showDetails ? (
        <DetailsMovie movie={selectedMovie} handleBackClick={handleBackClick} />
      ) : (
        <>
          <div className="carousel-grid">
            {(filteredMovies.length > 0 ? filteredMovies : movies).map((movie) => (
              <div key={movie.id} className="carousel-content">
                <Movie movie={movie} handleDetailsVisibility={handleDetailsVisibility} />
              </div>
            ))}
          </div>
          {isLoading && <p>Loading...</p>}
          {!isLoading && (
            <div className="load-more-container">
              <Publicidad />
              <button className="load-more-button" onClick={handleLoadMore}>
                Ver Mas ↓
              </button>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default Carousel;
