import React, { useState } from 'react';
import './App.css';
import Movie from './componentes/movie';
import MovieDetails from './componentes/movieDetails';

const App = () => {
  const [movieDetailsVisible, setMovieDetailsVisible] = useState(false);

  const toggleMovieDetails = () => {
    setMovieDetailsVisible(!movieDetailsVisible);
  };

  return (
    <div>
      <Movie openModal={toggleMovieDetails} />
      {movieDetailsVisible && <MovieDetails />}
    </div>
  );
};

export default App;
