import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { generarNumero } from '..index.js';

const Serie = () => {
  const [movie, setMovie] = useState(null);
  const [numeroGenerado, setNumeroGenerado] = useState(generarNumero());

  const fetchMovie = async () => {
    try {
      const apiKey = '83bc0d3d812780eff004a2baed4eaf17';
      const movieId = numeroGenerado;
      const response = await axios.get(`https://api.themoviedb.org/3/movie/${movieId}?api_key=${apiKey}`);
      setMovie(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleClick = () => {
    setNumeroGenerado(generarNumero());
  };

  useEffect(() => {
    fetchMovie();
  }, [numeroGenerado]);

  if (!movie) {
    return <div>Cargando...</div>;
  }

  return (
    <div className="card">
      <img className="card-image" src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt={movie.title} />
      <div className="card-content">
        <h1 className="card-title">{movie.title}</h1>
        <button className="card-button" onClick={handleClick}>Generar nuevo n√∫mero</button>
        {/* Resto del contenido del componente */}
      </div>
    </div>
  );
};

export default Serie;
