import React, { useState, useEffect } from 'react';
import Modal from '../modal/modal'; 
import './comentarios.css';

const Comentarios = ({ idUsuario, movieId }) => {
  const [comentarios, setComentarios] = useState([]);
  const [nuevoComentario, setNuevoComentario] = useState('');
  const [mensajeModal, setMensajeModal] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:3010/comentario?id_pelicula=${movieId}`)
      .then(response => response.json())
      .then(data => {
        const comentarios = data.comentario || []; // Asegúrate de que data.comentarios sea un arreglo
        setComentarios(comentarios);
      })
      .catch(error => console.error('Error:', error));
  }, [movieId]);

  const handleClose = () => {
    setMensajeModal(null);
  };

  const enviarComentario = (event) => {
    setNuevoComentario(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch('http://localhost:3010/comentario/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ usuario_id: idUsuario, comentario: nuevoComentario, id_pelicula: movieId }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message);
      } else {
        // Actualizar el estado local de comentarios con el nuevo comentario
        setComentarios([...comentarios, nuevoComentario]);
        setNuevoComentario(''); // Limpiar el input después de enviar el comentario
      }
    } catch (error) {
      console.error('Error:', error);
      setMensajeModal(error.message);
    }
  };
  console.log(comentarios)
  return (
    <>
      <div className="comentarios">
     
      <div className="chat">
        <h3>Comentarios</h3>
        {comentarios.map((comentario, index) => (
          <div key={index} className="mensaje">
            • {comentario}
          </div>
        ))}
      </div>

        <form className="formulario-comentario" onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Escribe un comentario"
            value={nuevoComentario}
            onChange={enviarComentario}
          />
          <button type="submit">Enviar</button>
        </form>
      </div>
      {mensajeModal && <Modal mensaje={mensajeModal} handleClose={handleClose} />}
    </>
  );
};

export default Comentarios;
