import React from 'react';
import './menu.css';

const Menu = ({ genres, handleGenreClick, handleNewsClick, handleTopRatedClick }) => {
    return (
        <div className="genre-cord">
          <h3>Generos:</h3>
          {genres.map((genre) => (
            <button key={genre.id} onClick={() => handleGenreClick(genre.id)}>
              {genre.name}
            </button>
          ))}
          <div className="additional-buttons">
            <button className="news-button" onClick={handleNewsClick}>
              Noticias
            </button>
            <button className="top-rated-button" onClick={handleTopRatedClick}>
              Las m√°s votadas
            </button>
          </div>
        </div>
      );          
};

export default Menu;
