import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Movie = () => {
  const [movie, setMovie] = useState(null);
  const [showDetails, setShowDetails] = useState(false);
  const [rating, setRating] = useState(0);

  const fetchMovie = async () => {
    try {
      const movieId = Math.floor(Math.random() * 150) + 1;
      const apiKey = '83bc0d3d812780eff004a2baed4eaf17';
      const response = await axios.get(
        `https://api.themoviedb.org/3/movie/${movieId}?api_key=${apiKey}`
      );

      setMovie(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchMovie();
  }, []);

  const verDetalle = () => {
    setShowDetails(!showDetails);
  };

  const handleRatingChange = (event) => {
    setRating(Number(event.target.value));
  };

  if (!movie) {
    return <div>Cargando...</div>;
  }

  return (
    <div className="card">
      <img
        className="card-image"
        src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
        alt={movie.title}
      />
      <div className="card-content">
        <h1 className="card-title">{movie.title}</h1>
        <button className="card-button" onClick={verDetalle}>
          Ver más
        </button>
        <button className="card-button" onClick={() => setRating(0)}>
          Calificar
        </button>
        {showDetails && (
          <div>
            <h4>Sinopsis</h4>
            <p>{movie.overview}</p>
          </div>
        )}
        {rating > 0 && (
          <div>
            <h4>Calificación</h4>
            <div>
              {[1, 2, 3, 4, 5].map((star) => (
                <label key={star}>
                  <input
                    type="radio"
                    name="rating"
                    value={star}
                    checked={rating === star}
                    onChange={handleRatingChange}
                  />
                  {star} estrella{star !== 1 && 's'}
                </label>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Movie;
