import React, { useEffect, useState } from 'react';
import './detailsMovie.css';
import axios from 'axios';

const DetailsMovie = ({ movie, handleBackClick }) => {
  const { title, director, release_date, genres, overview, poster_path, vote_average, cast_id } = movie;
  const [additionalDetails, setAdditionalDetails] = useState({ duration: '', cast: [], language: '', countries: [] });

  useEffect(() => {
    const fetchAdditionalDetails = async () => {
      try {
        const apiKey = 'YOUR_API_KEY';
        const response = await axios.get(`https://api.themoviedb.org/3/movie/${cast_id}?api_key=${apiKey}`);
        const { runtime, credits, original_language, production_countries } = response.data;
        const cast = credits.cast.slice(0, 5); // Obtén los primeros 5 actores del reparto

        setAdditionalDetails({
          duration: runtime,
          cast: cast,
          language: original_language,
          countries: production_countries.map((country) => country.name),
        });
      } catch (error) {
        console.error(error);
      }
    };

    fetchAdditionalDetails();
  }, [cast_id]);

  return (
    <div className='detalle'>
      <img src={`https://image.tmdb.org/t/p/w500/${poster_path}`} alt={title} />
      <div className='contenido'>
        <h2>{title}</h2>
        <p>
          <strong>Fecha de estreno:</strong> {release_date}
        </p>
        <p>
          <strong>Género:</strong> {genres.map((genre) => genre.name).join(', ')}
        </p>
        <p>
          <strong>Duración:</strong> {additionalDetails.duration} minutos
        </p>
        <p>
          <strong>Idioma:</strong> {additionalDetails.language}
        </p>
        <p>
          <strong>País de origen:</strong> {additionalDetails.countries.length > 0 ? additionalDetails.countries[0] : ''}
        </p>
        <p>
          <strong>Sinopsis:</strong> {overview}
        </p>
        <p>
          <strong>Calificación:</strong> {vote_average}
        </p>
   
        <button onClick={handleBackClick}>Volver</button>
      </div>
    </div>
  );
};

export default DetailsMovie;
