import React, { useState, useEffect } from 'react';
import axios from 'axios';
import YouTube from 'react-youtube';
import './detailsMovie.css';
import Comentarios from '../comentarios/comentarios';

const DetailsMovie = ({ selectedMovieId, handleBackClick }) => {
  const [movie, setMovie] = useState(null);
  const [trailerId, setTrailerId] = useState(null);
  const [verMas, setVerMas] = useState(false);

  useEffect(() => {
    const fetchMovieDetails = async () => {
      try {
        const apiKey = '83bc0d3d812780eff004a2baed4eaf17';
        const response = await axios.get(
          `https://api.themoviedb.org/3/movie/${selectedMovieId}?api_key=${apiKey}`
        );
        setMovie(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    const fetchTrailer = async () => {
      try {
        const apiKey = '83bc0d3d812780eff004a2baed4eaf17';
        const response = await axios.get(
          `https://api.themoviedb.org/3/movie/${selectedMovieId}/videos?api_key=${apiKey}`
        );
        if (response.data.results.length > 0) {
          setTrailerId(response.data.results[0].key);
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchMovieDetails();
    fetchTrailer();
  }, [selectedMovieId]);

  const handleVerMasClick = () => {
    setVerMas(!verMas);
  };

  if (!movie) {
    return <div>Loading...</div>;
  }

  return (
    <div className="details-movie">
      <button onClick={handleBackClick} className="back-button">
        Volver
      </button>
      <h2>{movie.title}</h2>
      {trailerId && <YouTube videoId={trailerId} className="trailer" />}
      <div className="details-content">
        <div className="details-poster">
          <img
            src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}
            alt={movie.title}
          />
        </div>
        <div className="details-info">
          <p>{movie.overview}</p>
          <p>Fecha de lanzamiento: {movie.release_date}</p>
          <p>Puntuación: {movie.vote_average}</p>
          <p>Géneros: {movie.genres.map((genre) => genre.name).join(', ')}</p>
        </div>
      </div>
      <button onClick={handleVerMasClick} className="ver-mas-button">
        {verMas ? 'Ver menos' : 'Ver más'}
      </button>
      {verMas && (
        <div>
          <h3>Detalles adicionales</h3>
          <p>Idioma original: {movie.original_language}</p>
          <p>Duración: {movie.runtime} minutos</p>
        </div>
      )}
      <Comentarios />
    </div>
  );
};

export default DetailsMovie;
