// ...

const Carousel = ({ handleDetailsVisibility, showPublicidad, handlePublicidadVisibility }) => {
  // ...

  const handleMovieClick = (movie) => {
    setSelectedMovie(movie);
    setShowDetails(true);
    handleDetailsVisibility();
    handlePublicidadVisibility(false); // Ocultar la publicidad al mostrar DetailsMovie
  };

  const handleBackClick = () => {
    setSelectedMovie(null);
    setShowDetails(false);
    handleDetailsVisibility();
    handlePublicidadVisibility(true); // Mostrar la publicidad al ocultar DetailsMovie
  };

  // ...

  return (
    <div className="carousel">
      {showDetails ? (
        <DetailsMovie movie={selectedMovie} handleBackClick={handleBackClick} />
      ) : (
        <>
          {movies.map((movie) => (
            <div key={movie.id} className="carousel-content">
              <Movie movie={movie} handleMovieClick={handleMovieClick} />
            </div>
          ))}
          <button className="boton" onClick={fetchMovies}>
            &gt;
          </button>
        </>
      )}
    </div>
  );
};

