import React, { useState, useEffect } from 'react';
import axios from 'axios';
import YouTube from 'react-youtube';
import './detailsMovie.css';
import Comentarios from '../comentarios/comentarios';

const DetailsMovie = ({ movieId, handleBackClick }) => {
  const [trailerId, setTrailerId] = useState(null);
  const [verMas, setVerMas] = useState(false);
  const apiKey = '83bc0d3d812780eff004a2baed4eaf17'; 

  useEffect(() => {
    const fetchTrailer = async () => {
      try {
        const response = await axios.get(
          `https://api.themoviedb.org/3/movie/${movieId}/videos?api_key=${apiKey}`
        );
        const trailerData = response.data;
        const trailer = trailerData.results.find((result) => result.site === 'YouTube');
        if (trailer) {
          setTrailerId(trailer.key);
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchTrailer();
  }, [apiKey, movieId]);

  const handleToggleOverview = () => {
    setVerMas(!verMas);
  };

  return (
    <div className="detalle">
      <div className="trailer">
        {trailerId ? (
          <YouTube videoId={trailerId} opts={{ width: '500px', height: '350px' }} />
        ) : (
          <p>No se encontró el tráiler</p>
        )}
      </div>

      <div className="contenido">
        <h2>Título de la película</h2>
        <p>
          <strong>Sinopsis:</strong>{' '}
          {verMas ? 'Descripción completa de la película' : 'Descripción resumida de la película'}
          {'...'}
          {verMas ? (
            <a href="#!" onClick={handleToggleOverview}>
              Ver menos
            </a>
          ) : (
            <a href="#!" onClick={handleToggleOverview}>
              Ver más
            </a>
          )}
        </p>
        <div className="rating-container">
          <h3>Califica esta película:</h3>
          <div className="rating-inputs">
            <label>
              <input type="radio" name="rating" value={1} />
              1
            </label>
            <label>
              <input type="radio" name="rating" value={2} />
              2
            </label>
            <label>
              <input type="radio" name="rating" value={3} />
              3
            </label>
            <label>
              <input type="radio" name="rating" value={4} />
              4
            </label>
            <label>
              <input type="radio" name="rating" value={5} />
              5
            </label>
          </div>
        </div>
        <div className="button">
          <button className="vote">Votar</button>
          <button className="back" onClick={handleBackClick}>
            Volver
          </button>
        </div>
      </div>
      <div className="chatComentarios">
        <Comentarios />
      </div>
    </div>
  );
};

export default DetailsMovie;
