import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Movie from '../movie/movie';
import Publicidad from '../publicidad/publicidad';
import DetailsMovie from '../details/detailsMovie';
import Menu from '../menu/menu';
import './carousel.css';

const Carousel = () => {
  const [movies, setMovies] = useState([]);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [showDetails, setShowDetails] = useState(false);
  const [page, setPage] = useState(1);
  const [isLoading, setIsLoading] = useState(false);
  const [genres, setGenres] = useState([
    { id: 28, name: 'Acción' },
    { id: 16, name: 'Animación' },
    { id: 35, name: 'Comedia' },
    { id: 18, name: 'Drama' },
    { id: 14, name: 'Fantasia' },
    { id: 27, name: 'Terror' },
    { id: 878, name: 'Ciencia Ficción' },
    // Los números son los ID de cada género obtenidos de la API
  ]);
  const [selectedGenreId, setSelectedGenreId] = useState(null);

  const fetchMovies = async () => {
    setIsLoading(true);
    try {
      const apiKey = '83bc0d3d812780eff004a2baed4eaf17';
      const response = await axios.get(`https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}&page=${page}`);
      const moviesData = response.data.results;

      // Filtrar las películas por género
      const filteredMovies = selectedGenreId ? moviesData.filter((movie) => movie.genre_ids.includes(selectedGenreId)) : moviesData;

      if (page === 1) {
        // Si es la primera página, reemplazar las películas por las filtradas
        setMovies(filteredMovies);
      } else {
        // Si no es la primera página, agregar las películas filtradas al estado existente
        setMovies((prevMovies) => [...prevMovies, ...filteredMovies]);
      }

      setIsLoading(false);
    } catch (error) {
      console.error(error);
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchMovies();
  }, [page, selectedGenreId]);

  const handleDetailsVisibility = (movie) => {
    setSelectedMovie(movie);
    setShowDetails(true);
  };

  const handleBackClick = () => {
    setSelectedMovie(null);
    setShowDetails(false);
  };

  const handleLoadMore = () => {
    setPage((prevPage) => prevPage + 1);
  };

  const handleGenreClick = (genreId) => {
    setSelectedGenreId(genreId);
  };

  return (
    <div className="carousel">
      <Menu genres={genres} handleGenreClick={handleGenreClick} />

      {showDetails ? (
        <DetailsMovie movie={selectedMovie} handleBackClick={handleBackClick} />
      ) : (
        <>
          <div className="carousel-grid">
            {(filteredMovies.length > 0 ? filteredMovies : movies).map((movie) => (
              <div key={movie.id} className="carousel-content">
                <Movie movie={movie} handleDetailsVisibility={handleDetailsVisibility} />
              </div>
            ))}
          </div>
          {isLoading && <p>Loading...</p>}
          {!isLoading && (
            <div className="load-more-container">
              <Publicidad />
              <button className="load-more-button" onClick={handleLoadMore}>
                Ver Mas ↓
              </button>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default Carousel;
