import React, { useState, useEffect, useRef } from "react";
import "./header.css";
import logo from "../../imagenes/logo.png";
import axios from "axios";

const Header = ({
  handleFormVisibilityRegistro,
  handleFormVisibilityInicioSesion,
  handleDetailsVisibility,
}) => {
  const [inputValue, setInputValue] = useState("");
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const inputRef = useRef(null);

  useEffect(() => {
    // Agregar event listener al documento para detectar clics
    const handleClickOutside = (e) => {
      if (inputRef.current && !inputRef.current.contains(e.target)) {
        setShowSuggestions(false);
      }
    };

    document.addEventListener("click", handleClickOutside);

    return () => {
      document.removeEventListener("click", handleClickOutside);
    };
  }, []);

  const handleLogoClick = () => {
    window.location.reload();
  };

  const handleInputChange = async (e) => {
    const value = e.target.value;
    setInputValue(value);

    if (value.trim() === "") {
      setSuggestions([]);
      setShowSuggestions(false);
      return;
    }

    try {
      const response = await axios.get(
        `https://api.themoviedb.org/3/search/movie?api_key=83bc0d3d812780eff004a2baed4eaf17&query=${value}&language=es`
      );
      const movieData = response.data.results.slice(0, 5);
      setSuggestions(movieData);
      setShowSuggestions(true);
    } catch (error) {
      console.error(error);
    }
  };

  const handleSuggestionClick = (movieData) => {
    setInputValue(movieData.title);
    setShowSuggestions(false);
    handleDetailsVisibility(movieData);
  };

  return (
    <header>
      <div className="logoDiv">
        <button className="logoButton" onClick={handleLogoClick}>
          <img className="logoHeader" src={logo} alt="Logo" />
        </button>
      </div>

      <div className="input-group" ref={inputRef}>
        <input
          type="text"
          className="form-control input-lg"
          aria-label="Recipient's username"
          aria-describedby="button-addon2"
          value={inputValue}
          onChange={handleInputChange}
        />
        {showSuggestions && (
          <ul className="suggestions">
            {suggestions.map((movieData) => (
              <li
                key={movieData.id}
                className="suggestion"
                onClick={() => handleSuggestionClick(movieData)}
              >
                {movieData.title}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="login">
        <a className="dropbtn">Iniciar sesi√≥n / Registrarse</a>
        <div className="login-content">
          <button onClick={handleFormVisibilityInicioSesion}>Inicio</button>
          <button onClick={handleFormVisibilityRegistro}>Registrarse</button>
        </div>
      </div>
    </header>
  );
};

export default Header;
