import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Movie from '../movie/movie';
import DetailsMovie from '../details/detailsMovie';
import './carousel.css';

const Carousel = () => {
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [showDetails, setShowDetails] = useState(false);

  const fetchMovie = async (movieId) => {
    const apiKey = '83bc0d3d812780eff004a2baed4eaf17';
    const response = await axios.get(`https://api.themoviedb.org/3/movie/${movieId}?api_key=${apiKey}`);
    const movieData = response.data;
    return movieData;
  };

  useEffect(() => {
    // IDs de las películas a mostrar en cada div del carrusel
    const movieIds = [1, 2, 3, 4];
    Promise.all(movieIds.map((movieId) => fetchMovie(movieId)))
      .then((moviesData) => {
        setMovies(moviesData);
      })
      .catch((error) => {
        console.error('Error al obtener las películas:', error);
      });
  }, []);

  const handleDetailsVisibility = (movie) => {
    setSelectedMovie(movie);
    setShowDetails(true);
  };

  const handleBackClick = () => {
    setSelectedMovie(null);
    setShowDetails(false);
  };

  return (
    <div className="carousel">
      {showDetails ? (
        <DetailsMovie movie={selectedMovie} handleBackClick={handleBackClick} />
      ) : (
        <>
          <div className="carousel-content">
            {movies[0] && <Movie movie={movies[0]} handleDetailsVisibility={handleDetailsVisibility} />}
          </div>
          <div className="carousel-content">
            {movies[1] && <Movie movie={movies[1]} handleDetailsVisibility={handleDetailsVisibility} />}
          </div>
          <div className="carousel-content">
            {movies[2] && <Movie movie={movies[2]} handleDetailsVisibility={handleDetailsVisibility} />}
          </div>
          <div className="carousel-content">
            {movies[3] && <Movie movie={movies[3]} handleDetailsVisibility={handleDetailsVisibility} />}
          </div>
          <button className="boton" onClick={fetchMovies}>
            &gt;
          </button>
        </>
      )}
    </div>
  );
};

export default Carousel;
