import React from 'react';
import './detailsMovie.css';

const DetailsMovie = ({ movie, handleBackClick }) => {
  const { title, director, release_date, genres, overview, poster_path, vote_average, cast } = movie;

  return (
    <div className='detalle'>
      <img src={`https://image.tmdb.org/t/p/w500/${poster_path}`} alt={title} />
      <div className='contenido'>
        <h2 className='titulo'>{title}</h2>
        <p>
          <strong>Director:</strong> {director}
        </p>
        <p>
          <strong>Fecha de estreno:</strong> {release_date}
        </p>
        <p>
          <strong>Género:</strong> {genres.map((genre) => genre.name).join(', ')}
        </p>
        <p>
          <strong>Calificación:</strong> {vote_average}
        </p>
        <p>
          <strong>Reparto:</strong> {cast.join(', ')}
        </p>
        <p>
          <strong>Sinopsis:</strong> {overview}
        </p>
        <button className='volver-btn' onClick={handleBackClick}>
          Volver
        </button>
      </div>
    </div>
  );
};

export default DetailsMovie;
