import React, { useState } from 'react';
import Movie from '../movie/movie';
import './carousel.css';

const Carousel = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const movies = [
    { id: 1, title: 'Movie 1' },
    { id: 2, title: 'Movie 2' },
    { id: 3, title: 'Movie 3' },
    // Agrega más películas aquí
  ];

  const goToNextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide === movies.length - 1 ? 0 : prevSlide + 1));
  };

  const goToPreviousSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide === 0 ? movies.length - 1 : prevSlide - 1));
  };

  return (
    <div className="carousel">
      <div className="carousel-content">
        {movies.map((movie, index) => (
          <Movie key={movie.id} movie={movie} isActive={index === currentSlide} />
        ))}
      </div>
      <button className="boton boton-anterior" onClick={goToPreviousSlide}>&lt;</button>
      <button className="boton boton-siguiente" onClick={goToNextSlide}>&gt;</button>
    </div>
  );
}

export default Carousel;
